import React, { useEffect, useState } from 'react';
import Character from '../../components/Character';
import { fetchCharacter } from '../../services/api';
import './CharcterList.css';

const CharcterList = () => {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    fetchCharacter().then((data) => {
      setCharacters(data.results);
    });
  }, []);

  const renderAllChaacters = (items) => {
    return items.map((item) => {
      return <Character item={item} />;
    });
  };

  return (
    <>
      {characters && (
        <div className="items-container">{renderAllChaacters(characters)}</div>
      )}
    </>
  );
};

export default CharcterList;
