import React, { useEffect } from 'react';
import Box from '@mui/material/Box';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';

const FilterMovies = ({ handleSelectedMovie }) => {
  const [movie, setMovie] = React.useState('');

  const handleChange = (event) => {
    setMovie(event.target.value);
  };

  useEffect(() => {
    handleSelectedMovie(movie);
  }, [movie]);

  return (
    <div>
      <Box sx={{ maxWidth: 200 }}>
        <FormControl fullWidth>
          <InputLabel id="demo-simple-select-label">Movies</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={movie}
            label="Movie"
            onChange={handleChange}
          >
            <MenuItem value="Episode 1">Episode 1</MenuItem>
            <MenuItem value="Episode 2">Episode 2</MenuItem>
            <MenuItem value="Episode 3">Episode 3</MenuItem>
            <MenuItem value="Episode 4">Episode 4</MenuItem>
            <MenuItem value="Episode 5">Episode 5</MenuItem>
            <MenuItem value="Episode 6">Episode 6</MenuItem>
            <MenuItem value="Episode 7">Episode 7</MenuItem>
          </Select>
        </FormControl>
      </Box>
    </div>
  );
};

export default FilterMovies;
