import React, { useEffect, useState } from 'react';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { getSpeciesName, getFilmName } from '../../services/api';

import './Character.css';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

const Character = ({ item }) => {
  const [speciesName, setSpeciesName] = useState('');
  const [filmNames, setFilmNames] = useState([]);

  useEffect(() => {
    // species name
    if (item && item.species) {
      getSpeciesName(item.species[0]).then((species) => {
        setSpeciesName(species.name);
      });
    }

    // movies name
    if (item && item.films) {
      getFilmName(item.films).then((names) => {
        setFilmNames(names);
      });
    }
  }, [item]);

  const renderFilmNames = (films) => {
    return films.map((film) => {
      return <span key={film}>{film}</span>;
    });
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <div className="character-container">
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1a-content"
            id="panel1a-header"
          >
            <Typography>{item.name}</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <Typography>Name: {item.name}</Typography>
            <Typography>Species: {speciesName}</Typography>

            <Typography>Movies:{renderFilmNames(filmNames)}</Typography>
            {/* <Typography>Spaceships: {item.starships}</Typography> */}
          </AccordionDetails>
        </Accordion>
      </div>
    </ThemeProvider>
  );
};

export default Character;
