import React, { useEffect, useState } from 'react';
import Character from '../../components/Character';
import FilterSpecies from '../FilterSpecies';
import FilterMovies from '../FilterMovies';
import Dropdown from '../../components/Header/Dropdown';
import { fetchCharacter, getSpecies } from '../../services/api';
import { species, movies } from '../../constants';
import './CharacterList.css';

const CharcterList = () => {
  const [characters, setCharacters] = useState([]);
  const [selectedSpecies, setSelectedSpecies] = useState('');
  const [selectedMovie, setSelectedMovie] = useState('');

  useEffect(() => {
    fetchCharacter().then((data) => {
      setCharacters(data.results);
    });

    // getSpecies();
  }, []);

  const handleSelectedSpecies = (value) => {
    setSelectedSpecies(value);
  };

  const handleSelectedMovie = (value) => {
    setSelectedMovie(value);
  };

  const renderAllChaacters = (items) => {
    return items.map((item) => {
      return (
        <Character
          item={item}
          filterSpecies={selectedSpecies}
          filterMovie={selectedMovie}
        />
      );
    });
  };

  return (
    <div className="container">
      {characters && (
        <div className="items-container">
          <div className="charcters-title">Characters</div>
          {renderAllChaacters(characters)}
        </div>
      )}
      <div className="filter-container">
        <div className="filter-title">Filter</div>
        <FilterSpecies handleSelectedSpecies={handleSelectedSpecies} />
        <FilterMovies handleSelectedMovie={handleSelectedMovie} />
        <Dropdown
          handleSelection={handleSelectedMovie}
          menuItems={['1', '2']}
          label="name"
        />
      </div>
    </div>
  );
};

export default CharcterList;
